import { useState } from "react";
import "./Tarefa.css";

export default function ComponenteTarefa({ tarefa, onExcluir, onToggleConclusao, onEditar }) {
  const isConcluida = tarefa.status === "conclu√≠da";

  // A classe 'concluida' s√≥ ser√° aplicada se a tarefa estiver conclu√≠da
  return (
    <div className={`tarefa ${isConcluida ? "concluida" : ""}`}>
      <input
        type="checkbox"
        checked={isConcluida}
        onChange={() => onToggleConclusao(tarefa)}  // Passando o objeto 'tarefa' completo para manipula√ß√£o
      />
      <div className="info">
        <strong>{tarefa.titulo}</strong>
        <p>{tarefa.descricao}</p>
        <small>
          Prioridade: {tarefa.prioridade} | Status: {tarefa.status}
        </small>
      </div>
      <button className="editar" onClick={() => onEditar(tarefa)}>
        ‚úèÔ∏è Editar Tarefa
      </button>

      <button className="excluir" onClick={() => onExcluir(tarefa)}>
        üóëÔ∏è Excluir
      </button>
    </div>
  );
}
