import { useEffect, useState } from "react";
import { supabase } from "../../supabaseClient"; // Importa a inst칙ncia do Supabase
import "./menu.css";

export default function TopBar() {
  const [usuarioNome, setUsuarioNome] = useState(""); // Estado para armazenar o nome do usu치rio

  useEffect(() => {
    // Fun칞칚o para pegar o usu치rio logado
    const usuario = supabase.auth.user();

    if (usuario) {
      setUsuarioNome(usuario.user_meta); // Aqui, pode ser alterado para 'usuario.nome' se o nome estiver no perfil do usu치rio
    } else {
      setUsuarioNome("Usu치rio n칚o autenticado");
    }
  }, []); // O useEffect roda apenas uma vez quando o componente 칠 montado

  return (
    <div className="topbar">
      <h1>Bem-vindo, {usuarioNome}!</h1> {/* Exibe o nome do usu치rio */}
      <div className="usuario">
        <span>游녻 {usuarioNome}</span> {/* Exibe o nome do usu치rio na tag span */}
      </div>
    </div>
  );
}
