import { useState } from "react";
import "./Tarefa.css";

export default function FormularioTarefa({ onSalvar, tarefa }) {
  const [titulo, setTitulo] = useState("");
  const [descricao, setDescricao] = useState("");
  const [prioridade, setPrioridade] = useState("média");
  const [status, setStatus] = useState("pendente")

  const handleSubmit = (e) => {
    e.preventDefault();

      const novaTarefa = {
        id: tarefa ? tarefa.id : Date.now(),
        descricao,
        concluida: tarefa ? tarefa.concluida : false,
      };
      onSalvar(novaTarefa);
      setDescricao("");

  };

  return (
    <form className="formulario-tarefa" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Descrição da tarefa"
        value={descricao}
        onChange={(e) => setDescricao(e.target.value)}
        required
      />
      <button type="submit">{tarefa ? "Atualizar" : "Salvar"}</button>
    </form>
  );
}
