import { useEffect, useState } from "react";
import { supabase } from "../../supabaseClient";
import "./menu.css";

export default function TopBar() {
  const [usuarioNome, setUsuarioNome] = useState(""); // Estado para armazenar o nome do usu√°rio

  useEffect(() => {
    // Fun√ß√£o ass√≠ncrona para pegar o usu√°rio logado
    const fetchUser = async () => {
      const { data: { user } } = await supabase.auth.getUser(); // Usando getUser() no lugar de user()

      if (user) {
        setUsuarioNome(user.nome); // Aqui voc√™ pode acessar o nome ou qualquer outro dado do perfil
      } else {
        setUsuarioNome("Usu√°rio n√£o autenticado");
      }
    };

    fetchUser(); // Chama a fun√ß√£o para buscar o usu√°rio
  }, []); // O useEffect roda apenas uma vez quando o componente √© montado

  return (
    <div className="topbar">
      <h1>Bem-vindo, {usuarioNome}!</h1> {/* Exibe o nome do usu√°rio */}
      <div className="usuario">
        <span>üë§ {usuarioNome}</span> {/* Exibe o nome do usu√°rio na tag span */}
      </div>
    </div>
  );
}
