    const usuario = supabase.auth.user();

    if (usuario) {
      setUsuarioNome(usuario.email); // Aqui, pode ser alterado para 'usuario.nome' se o nome estiver no perfil do usu치rio
    } else {
      setUsuarioNome("Usu치rio n칚o autenticado");
    }
  },)

  return (
    <div className="topbar">
      <h1>Bem-vindo!</h1>
      <div className="usuario">
        <span>游녻 Usu치rio</span>
      </div>
    </div>
  );
}
