import { useState } from "react";
import "./Tarefa.css";

export default function ComponenteTarefa({ tarefa, onExcluir, onToggleConclusao, onEditar}) {
  const isConcluida = tarefa.status === "conclu√≠da";

  return (
    <div className={`tarefa ${isConcluida ? "concluida" : ""}`}>
      <input 
        type="checkbox" 
        checked={isConcluida} 
        onChange={onToggleConclusao} 
      />
       <div className="info">
        <strong>{tarefa.titulo}</strong>
        <p>{tarefa.descricao}</p>
        <small>Prioridade: {tarefa.prioridade} | Status: {tarefa.status}</small>
      </div>
      <button 
        className="editar" 
        onClick={() => onEditar(tarefa)}>
          ‚úèÔ∏è Editar Tarefa
      </button>
      
      <button 
        className="excluir" 
        onClick={() => onExcluir(tarefa.id)}>
          üóëÔ∏è Excluir
      </button>
    </div>
  );
}
