import React, { useState } from "react";
import { Titulo, Botao, CaixaTexto } from "../../Componentes/ComponentesIcones";
import "./cadastro.css"

export default function Cadastro() {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [confirmarSenha, setConfirmarSenha] = useState("");

  return (
    <div className="cadastro-container">
      <Titulo text="Criar Conta" />

      <form className="cadastro-form">
        {/* onSubmit={handleSubmit} */}

        <div className="form-group">
          <label htmlFor="email">Informe o seu E-mail:</label>
          <CaixaTexto
            id="email"
            text="Digite seu email"
            value={email}
            type="email"
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="senha">Informe a sua Senha:</label>
          <CaixaTexto
            id="senha"
            type="password"
            value={senha}
            text="Digite sua senha"
            onChange={(e) => setSenha(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="confirmarSenha">Confirme sua Senha:</label>
          <CaixaTexto
            id="confirmarSenha"
            type="password"
            value={confirmarSenha}
            text="Digite sua senha novamente"
            onChange={(e) => setConfirmarSenha(e.target.value)}
            required
          />
        </div>

        <div className="botoes-cadastro">
          <Botao text="Cadastrar" />
          <Botao text="Voltar à tela de Login" type="button" />
          <Link to="/">
            <BotaoClicado>Cadastre-se agora</BotaoClicado>
          </Link>
        </div>
      </form>
    </div>
  );
}
