import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { supabase } from "../../supabaseClient";
import { Titulo, Botao, BotaoClicado, CaixaTexto } from "../../Componentes/ComponentesIcones";
import "./meuCadastro.css";

export default function MeuCadastro() {
  const [nome, setNome] = useState("");
  const [senha, setSenha] = useState("");
  const [erro, setErro] = useState(null);
  const navigate = useNavigate();

  // Usando useEffect para pegar as informações do usuário logado
  useEffect(() => {
    const fetchUserData = async () => {
      const { data: { user }, error } = await supabase.auth.getUser();
      
      if (error) {
        setErro("Erro ao carregar os dados do usuário.");
        return;
      }

      if (user) {
        // Preenche o campo 'nome' com o valor atual do nome do usuário
        setNome(user.email); // Ou use 'user.user_metadata' para mais detalhes, se aplicável
      } else {
        navigate("/"); // Se o usuário não estiver logado, redireciona para o login
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErro(null);

 

    // Obtém o usuário logado
    const { data: { user }, error } = await supabase.auth.getUser();

    if (error || !user) {
      setErro("Usuário não está logado.");
      return;
    }

    try {
      // Atualizando os dados do usuário na tabela 'usuarios'
      const { error: updateError } = await supabase
        .from("usuarios")
        .update({ nome, senha }) // Atualiza nome e senha
        .eq("iduser", user.id); // Usando o ID do usuário para realizar a atualização

      if (updateError) {
        console.error("Erro ao atualizar no banco:", updateError);
        setErro("Erro ao atualizar informações no banco de dados.");
      } else {
        // Atualizando a senha do usuário no Supabase
        if (senha) {
          const { error: passwordError } = await supabase.auth.updateUser({
            password: senha, // Atualiza a senha do usuário no Supabase
          });

          if (passwordError) {
            setErro("Erro ao atualizar a senha.");
            return;
          }
        }

        // Após atualizar as informações, redireciona para a tela inicial
        navigate("/");
      }
    } catch (err) {
      console.error("Erro inesperado:", err);
      setErro("Erro inesperado.");
    }
  };

  return (
    <div className="cadastro-container">
      <Titulo text="Atualizar Conta" />

      <form onSubmit={handleSubmit} className="cadastro-form">
        {erro && <p className="erro">{erro}</p>}

        <div className="form-group">
          <label htmlFor="nome">Nome completo:</label>
          <CaixaTexto
            id="nome"
            text="Digite seu nome"
            value={nome}
            type="text"
            onChange={(e) => setNome(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="senha">Senha:</label>
          <CaixaTexto
            id="senha"
            type="password"
            value={senha}
            text="Digite sua senha"
            onChange={(e) => setSenha(e.target.value)}
          />
        </div>

        <div className="botoes-cadastro">
          <Botao text="Atualizar" type="submit" />
          <Link to="/dashboard">
            <BotaoClicado>Voltar à tela Inicial</BotaoClicado>
          </Link>
        </div>
      </form>
    </div>
  );
}
