import { useState, useEffect } from "react";
import FormularioTarefa from "../../Componentes/Tarefas/formularioTarefa";
import ListaTarefas from "../../Componentes/Tarefas/listaTarefas";
import "./dashboard.css";
import { Titulo, Botao } from "../../Componentes/ComponentesIcones";
import { supabase } from "../../supabaseClient";

export default function DashboardTarefas() {
  const [tarefas, setTarefas] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [tarefaEditando, setTarefaEditando] = useState(null);

  useEffect(() => {
    carregarTarefas();
  }, []);

  const carregarTarefas = async () => {
    const { data, error } = await supabase
      .from("tarefas")
      .select("*")
      .order("descricao");

    if (error) {
      console.error("Erro ao carregar tarefas:", error);
    } else {
      setTarefas(data);
    }
  };

  const salvarTarefa = async (tarefa) => {
    if (!tarefa.descricao || tarefa.descricao.trim() === "") {
      alert("A descrição da tarefa não pode estar vazia.");
      return;
    }

    let error;
    if (tarefaEditando) {
      ({ error } = await supabase
        .from("tarefas")
        .update({
          descricao: tarefa.descricao,
          concluida: tarefa.concluida,
        })
        .eq("id", tarefa.id));
    } else {
      ({ error } = await supabase
        .from("tarefas")
        .insert([{ descricao: tarefa.descricao, concluida: false }]));
    }

    if (error) {
      console.error("Erro ao salvar tarefa:", error);
    }

    setTarefaEditando(null);
    setMostrarFormulario(false);
    carregarTarefas();
  };

  const alternarConclusao = async (id, concluidaAtual) => {
    const { error } = await supabase
      .from("tarefas")
      .update({ concluida: !concluidaAtual })
      .eq("id", id);

    if (error) console.error("Erro ao atualizar status:", error);
    carregarTarefas();
  };

  const excluirTarefa = async (id) => {
    const { error } = await supabase.from("tarefas").delete().eq("id", id);
    if (error) console.error("Erro ao excluir tarefa:", error);
    carregarTarefas();
  };

  const editarTarefa = (tarefa) => {
    setTarefaEditando(tarefa);
    setMostrarFormulario(true);
  };

  const progresso = tarefas.length
    ? (tarefas.filter((t) => t.concluida).length / tarefas.length) * 100
    : 0;

  return (
    <div className="dashboard-tarefas">
      <div className="cabecalho">
        <Titulo text="Minhas Tarefas" />
        <Botao
          text="+ Nova Tarefa"
          onClick={() => {
            setTarefaEditando(null);
            setMostrarFormulario(!mostrarFormulario);
          }}
        />
      </div>

      <div className="progresso">
        <label>Progresso do Projeto</label>
        <progress value={progresso} max="100" />
      </div>

      {mostrarFormulario && (
        <FormularioTarefa
          onSalvar={salvarTarefa}
          tarefa={tarefaEditando}
        />
      )}

      <ListaTarefas
        tarefas={tarefas}
        onExcluir={excluirTarefa}
        onToggleConclusao={(id) => {
          const tarefa = tarefas.find((t) => t.id === id);
          if (tarefa) alternarConclusao(id, tarefa.concluida);
        }}
        onEditar={editarTarefa}
      />
    </div>
  );
}
