import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { supabase } from "../../supabaseClient";
import { Titulo, Botao, BotaoClicado, CaixaTexto } from "../../Componentes/ComponentesIcones";
import "./meuCadastro.css";

export default function MeuCadastro() {
  const [nome, setNome] = useState("");
  const [senha, setSenha] = useState("");
  const [confirmarSenha, setConfirmarSenha] = useState("");
  const [erro, setErro] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Verifica se o usuário está logado
    const user = supabase.auth.user();
    
    if (user) {
      // Se o usuário estiver logado, preenche os campos com os dados existentes
      setNome(user.email); // Assumindo que o nome do usuário está no email, mas pode ser alterado
    } else {
      navigate("/"); // Redireciona para a página de login se o usuário não estiver autenticado
    }
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErro(null);

    // Verificando se as senhas coincidem
    if (senha !== confirmarSenha) {
      setErro("As senhas não coincidem.");
      return;
    }

    // Obtém o usuário logado
    const user = supabase.auth.user();

    if (!user) {
      setErro("Usuário não está logado.");
      return;
    }

    try {
      // Atualiza as informações do usuário na tabela "usuarios"
      const { error } = await supabase
        .from("usuarios")
        .update({ nome, senha }) // Atualiza nome e senha
        .eq("iduser", user.id); // Utiliza o ID do usuário logado para realizar a atualização

      if (error) {
        console.error("Erro ao atualizar no banco:", error);
        setErro("Erro ao atualizar informações no banco de dados.");
      } else {
        // Redireciona para a tela inicial após sucesso
        navigate("/");
      }
    } catch (err) {
      console.error("Erro inesperado:", err);
      setErro("Erro inesperado.");
    }
  };

  return (
    <div className="cadastro-container">
      <Titulo text="Atualizar Conta" />

      <form onSubmit={handleSubmit} className="cadastro-form">
        {erro && <p className="erro">{erro}</p>}

        <div className="form-group">
          <label htmlFor="nome">Nome completo:</label>
          <CaixaTexto
            id="nome"
            text="Digite seu nome"
            value={nome}
            type="text"
            onChange={(e) => setNome(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="senha">Senha:</label>
          <CaixaTexto
            id="senha"
            type="password"
            value={senha}
            text="Digite sua senha"
            onChange={(e) => setSenha(e.target.value)}
            required
          />
        </div>

        <div className="botoes-cadastro">
          <Botao text="Atualizar" type="submit" />
          <Link to="/dashboard">
            <BotaoClicado>Voltar à tela Inicial</BotaoClicado>
          </Link>
        </div>
      </form>
    </div>
  );
}
